document.addEventListener("DOMContentLoaded",(function(){const e=document.querySelector(".menu-toggle"),t=document.querySelector(".nav-list"),o=document.querySelector(".back-to-top"),n=document.querySelector(".header");e&&e.addEventListener("click",(function(){this.classList.toggle("active"),t.classList.toggle("active")}));document.querySelectorAll(".nav-list a").forEach((o=>{o.addEventListener("click",(function(){window.innerWidth<=768&&(e&&e.classList.remove("active"),t.classList.remove("active"))}))})),window.addEventListener("scroll",(function(){window.pageYOffset>300?o.classList.add("visible"):o.classList.remove("visible"),window.pageYOffset>50?n.classList.add("scrolled"):n.classList.remove("scrolled")})),o.addEventListener("click",(function(){window.scrollTo({top:0,behavior:"smooth"})})),document.querySelectorAll('a[href^="#"]').forEach((e=>{e.addEventListener("click",(function(e){e.preventDefault();const t=this.getAttribute("href");if("#"===t)return;const o=document.querySelector(t);if(o){const e=document.querySelector(".header").offsetHeight,t=o.getBoundingClientRect().top+window.pageYOffset-e;window.scrollTo({top:t,behavior:"smooth"})}}))}));const i=function(){document.querySelectorAll(".section").forEach((e=>{e.getBoundingClientRect().top<.75*window.innerHeight&&e.classList.add("visible")}))};window.addEventListener("load",i),window.addEventListener("scroll",i)}));
